import React from "react";


const TasksFilterAll =({done,onAllVision}) =>  {

    let classNames = ""
    if (done) {
        classNames += 'hidden';
    };

            return (
            <li className={classNames}>
                 <button
                    onClick = {() => onAllVision()}>
                    All
                 </button>
             </li>);
};

const TasksFilterCompleted =() =>  {
    return (
        <li>
            <button
                onClick = {() => console.log('Completed')}>
                Completed
            </button>
        </li>);
};

const TasksFilterActive =({onActiveVision,done}) =>  {

    let classNames = ""
    if (done) {
        classNames += 'hidden';
    };

    return (
        <li className={classNames}>
            <button
                onClick = {()=>onActiveVision()}>
                Active
            </button>
        </li>);
};

const TasksFilterClear =({onClearCompleted}) => {
        return (
            <button
                onClick = {()=>onClearCompleted()}>
                Clear Completed
            </button>
        );
};

export {
    TasksFilterAll,TasksFilterCompleted,TasksFilterActive,TasksFilterClear
}
