import React from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

 let TaskCreate =`created ${formatDistanceToNow (new Date())} ago`;

//=> 'less than 20 seconds'

 const Task = ({label,  onDeleted, onActiveCounter, completed,onActiveItem}) =>{

        // state =  {
        //     completed: false,
        // };
        //
        // onLabelClick=()=> {
        //     this.setState((state)=>{
        //        return {
        //            completed: !state.completed
        //        };
        //     });
        // };

        // const {label,  onDeleted, onActiveCounter, completed} = this.props

        let classNames = ""
        if (completed) {
            classNames += 'completed';
        };


        return (

            <li className={classNames}>
                <div className="view">
                    <input className="toggle" type="checkbox"/>
                        <label >
                            <span
                              className='description'

                              onClick = {onActiveCounter}  >
                              {label}
                            </span>
                                <span className="created">{TaskCreate}</span>
                            </label>
                    <button className="icon icon-edit"></button>
                    <button type ='button'
                            className="icon icon-destroy"
                            onClick = {onDeleted}>

                    </button>
                </div>
            </li>

        );

};

export default Task;
