import React from "react";

import {TasksFilter, TasksFilterClear} from "./TasksFilter";
import Task from "./Task";



const Footer = () => {
    const elements = todos.map((item)=>{

        const {id, ...itemProps} = item;

        return(
            <Task
                {...item}
                key={item.id}
                onDeleted ={()=>onDeletedId(id)}/>
        );
    });
    return (
        <footer className="footer">
            <span className="todo-count">1 items left</span>
            <ul className="filters">
                <TasksFilter className="selected" label = "All"/>
                <TasksFilter label = "Active"/>
                <TasksFilter label = "Completed"/>
            </ul>

             <TasksFilterClear className = "clear-completed"
             label = "Clear completed"/>

        </footer>
    );
};

export default Footer;
