import React from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'



export default class Task extends React.Component{




    render() {
        const {label,  onDeleted, onActiveCounter, done, time} = this.props;
        let classNames = ""
        if (done) {
            classNames += 'completed';
        };

        let TaskCreateTime =`created ${formatDistanceToNow ((new Date(time)),{includeSeconds: true})} ago`;
        return (

            <li className={classNames}>
                <div className="view">
                    <input className="toggle" type="checkbox"/>
                    <label >
                        <span
                            className='description'
                            onClick = {onActiveCounter}  >
                          {label}
                        </span>
                        <span className="created">{TaskCreateTime}</span>
                    </label>
                    <button className="icon icon-edit"></button>
                    <button type ='button'
                            className="icon icon-destroy"
                            onClick = {onDeleted}>

                    </button>
                </div>
            </li>

        );
    }
}
