    //     this.setState({ label: '' });
    //     const cb = this.props.onLabelChange;
    //     cb(this.props.id, label);
    // };




    // componentDidMount() {
    //     const {updateInterval} = this.props
    //     this.timerID = setInterval(() => this.tick(), updateInterval)
    // }
    //
    // componentWillUnmount() {
    //     clearInterval(this.timerID)
    // }

    EditItemChange = (e)=>{
        console.log ("XYN", e.target.value)
        this.setState({
            label: e.target.value
        });
    };

    render() {

        const {label,  onDeleted, onActiveCounter, done, checked} = this.props;
        let classNames = ""
        if (done) {
            classNames += 'completed';
        };

        return (

            <li className={classNames}>
                <div className="view">
                    <input
                        className="toggle"
                        type="checkbox"
                        onClick = {onActiveCounter}
                        checked ={checked}
                    />
                    <label >
                        <span className = 'description'>
                          {label}
                        </span>
                        <span className="created">{this.tick()}</span>
                    </label>
                    <button className="icon icon-edit"
                            >
                        <input
                            onChange = {this.EditItemChange}
                            />
                    </button>
                    <button type ='button'
                            className="icon icon-destroy"
                            onClick = {onDeleted}>

                    </button>
                </div>
            </li>
        );
    };
};
