import React from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'


export default class Task extends React.Component{




    state = {
        date: false,
    }

    tick() {
        this.setState({ date: formatDistanceToNow(this.props.time, { includeSeconds: true })})
    }

    componentDidMount() {
        const {updateInterval} = this.props
        this.timerID = setInterval(() => this.tick(), updateInterval)
    }

    componentWillUnmount() {
        clearInterval(this.timerID)
    }

    render() {
        const {label,  onDeleted, onActiveCounter, done} = this.props;
        let classNames = ""
        if (done) {
            classNames += 'completed';
        };

        Task.defaultProps = {
            updateInterval:2000
        };

        Task.propTypes = {
            updateInterval:(props,propName,componentName) => {
            const value = props[propName];

            if(typeof value == 'number' && !isNaN(value)){
                return null;
            }
            return new TypeError(`${componentName}:${propName} must be number`)
            }
    };

        let  classNamesCheckBox = "toggle"
        if (done) {
            classNamesCheckBox += ' ';
        };

        return (

            <li className={classNames}>
                <div className="view">
                    <input
                        className={classNamesCheckBox}
                           type="checkbox"
                           onClick = {onActiveCounter}/>
                    <label >
                        <span
                            className='description'
                              >
                          {label}
                        </span>
                        <span className="created">{this.state.date}</span>
                    </label>
                    <button className="icon icon-edit"></button>
                    <button type ='button'
                            className="icon icon-destroy"
                            onClick = {onDeleted}>

                    </button>
                </div>
            </li>
        );
    };
};
