import React from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'


 let TaskCreate =`created ${formatDistanceToNow (new Date(2020,11,20,0,5,50))} ago`;
    let TaskCreateW = setInterval(TaskCreate,2000)
// let TaskCreate  = new Date().toLocaleString()

//=> 'less than 20 seconds'

 const Task = ({label,  onDeleted, onActiveCounter, done}) =>{


        let classNames = ""
        if (done) {
            classNames += 'completed';
        };


        return (

            <li className={classNames}>
                <div className="view">
                    <input className="toggle" type="checkbox"/>
                        <label >
                            <span
                              className='description'
                              onClick = {onActiveCounter}  >
                              {label}
                            </span>
                                <span className="created">{console.log(TaskCreate)},{TaskCreate}</span>
                            </label>
                    <button className="icon icon-edit"></button>
                    <button type ='button'
                            className="icon icon-destroy"
                            onClick = {onDeleted}>

                    </button>
                </div>
            </li>

        );

};

export default Task;
