import React from "react";

const NewTaskForm = ({onItemAdd}) => {
    return (
        <header className="header">
            <h1>todos</h1>

            <form >
                <input
                    onClick = {()=>onItemAdd("New element")}
                    className="new-todo"
                    required
                    placeholder="What needs to be done?"/>
            </form>
        </header>

    );
};

export default NewTaskForm;
