import React, {Component} from "react";

class TasksFilter extends Component  {
    render() {
        const {label} = this.props
            return (
            <li>
                 <button
                    onClick = {()=>console.log(`Done:${label}`)}>
                    {label}
                </button>
             </li>);
    };
};

class TasksFilterClear extends Component {



    render() {

        const {id} = this.props.state
        
        console.log(this.props)
        const {label} = this.props
        return (
            <button
                onClick = {(id) => this.props.onClearCompleted(id)}>
                {label}
            </button>
        );
    };
};

export {
TasksFilter,TasksFilterClear
}
