import React from 'react';
import NewTaskForm from "../NewTaskForm";

import './App.css'
import TaskList from "../TaskList";
import Footer from "../Footer";


export default class App extends React.Component {


    maxId = 100;
    state = {
        todoData: [
            {label: "Task 1",
                done: false,
                id: this.maxId++,
                shouldHide:false,},
            {label: "Task 2",
                done: false,
                id: this.maxId++,
                shouldHide:false,},
            {label: "Task 3",
                done: false,
                id: this.maxId++,
                shouldHide:false,},
        ]

    };

    // createTodoItem (label) {
    //     return {
    //         label,
    //         done: false,
    //         id: this.maxId++,
    //         shouldHide:false,
    //     };
    // };

    deleteItem = (id) =>{

        this.setState(({todoData})=>{

            const idx = todoData.findIndex((el)=>el.id===id);
            const newArray = [...todoData.slice(0,idx),...todoData.slice(idx+1)];
           return {
               todoData:newArray
           };

        });

    };

    addItem = (text)=>{
         const newItem = this.createTodoItem(text);

         this.setState(({todoData})=>{
            const newArr = [
                ...todoData,
                newItem
             ];
             return {todoData: newArr}

         });
    };

    onActiveCounter = (id) => {

        this.setState(({ todoData })=>{
            const idx = todoData.findIndex((el)=>el.id===id);

            const oldItem = todoData[idx];
            const newItem = {...oldItem,
                done:!oldItem.done};

            const newArray = [...todoData.slice(0,idx),
                newItem,
                ...todoData.slice(idx+1)
            ];
           
            return {
                todoData:newArray
            };
        });

    };

    onAllVision = () => {

            console.log(this.state.todoData)

            return (this.state.todoData);

    };

    onActiveVision = () => {

        this.setState(({ todoData })=>{
            const newArray = todoData.map((el)=> {
                if (el.done) {
                    el.shouldHide=!el.shouldHide
                }
            });

            console.log("Active completed", newArray,{ todoData })
            return {
                newArray
            };
        });
    };

    onCompletedVision = () => {

        this.setState(({ todoData })=>{

            const newArray = todoData.map((el)=> {
                if (el.done) {
                   el.shouldHide=!el.shouldHide
                }
            });


            console.log("vision completed", newArray)
            return {
                newArray
            };
        });

    };

    onClearCompleted = () => {
        this.setState(({todoData})=> {
            const newArray = todoData.filter(el => el.done === false);
            console.log("clearCompleted", newArray)
            return ({todoData: newArray});
        });
    };

    render() {

        const { todoData } = this.state;
        const doneItem = todoData.filter((el)=> el.done).length;
        const activeItem = todoData.length-doneItem;

        return(
            <section className="todoapp">
                <NewTaskForm
                    activeItem={activeItem}
                    onItemAdd = {this.addItem}/>
                <TaskList
                    todos = {todoData}
                    onDeletedId={this.deleteItem}
                    onActiveCounter={this.onActiveCounter}/>
                <Footer
                    activeItem={activeItem}
                    onClearCompleted={this.onClearCompleted}
                    onActiveVision={this.onActiveVision}
                    onAllVision={this.onAllVision}
                    onCompletedVision={this.onCompletedVision}/>
            </section>
        );
    };

};
