import React, {Component} from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

 let TaskCreate =`created ${formatDistanceToNow (new Date())} ago`;

//=> 'less than 20 seconds'

export default class Task extends Component {
    render () {

        const {label,  important= false} = this.props

        const liCompletedStyle = {

            liCompleted: important ? 'completed' :null,

        };
        return (

            <div className="view">
                <input className="toggle" type="checkbox"/>
                <label>
                    <span className={`description
                          ${liCompletedStyle.liCompleted}`}
                          onClick = {()=>console.log(`Done:${label}`)}>
                          {label}
                    </span>
                    <span className="created">{TaskCreate}</span>
                </label>
                <button className="icon icon-edit"></button>
                <button className="icon icon-destroy"></button>
            </div>
        );
    };
};
